package com.byteobject.prototype.leetcode;

public class LongestPalindromicString {

    public static void main(String[] args) {
        System.out.println(longestPalindrome(""));
    }

    public static String longestPalindrome(String s) {
        String longestPalindromeSubString = "";
        for (int i = 0; i < s.length(); i++) {
            for (int j = i; j < s.length(); j++) {
                String subString = s.substring(i, j+1);
                String reversed = reverse(subString);
                if (subString.equals(reversed) && subString.length() > longestPalindromeSubString.length())
                    longestPalindromeSubString = subString;
            }
        }

        return longestPalindromeSubString;
    }

    public static String reverse(String s) {
        char[] chars = s.toCharArray();
        char[] reverse = new char[s.length()];

        for (int i = 0; i < s.length(); i++) {
            reverse[i] = chars[s.length() - (i + 1)];
        }

        return new String(reverse);
    }

}
